customCSS:
/* Alien Invasion Theme - Compatible with next-themes (light/dark) */
/* Uses data-site-theme and --site-* variables to avoid conflicts */

/* Neon glow effects */
:root[data-site-theme='alien-invasion'] .challenge-card,
:root[data-site-theme='alien-invasion'] .rounded-lg.border {
  box-shadow: 0 0 10px rgba(57, 255, 20, 0.3);
  transition: all 0.3s ease;
}

:root[data-site-theme='alien-invasion'] .challenge-card:hover,
:root[data-site-theme='alien-invasion'] .rounded-lg.border:hover {
  box-shadow: 0 0 20px rgba(57, 255, 20, 0.5), 0 0 30px rgba(57, 255, 20, 0.3);
  transform: translateY(-2px);
}

/* Animated floating effect */
@keyframes alien-float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

:root[data-site-theme='alien-invasion'] .alien-float {
  animation: alien-float 6s ease-in-out infinite;
}

/* Pulsing glow for primary buttons */
@keyframes alien-pulse-glow {
  0%, 100% { box-shadow: 0 0 10px rgba(57, 255, 20, 0.5); }
  50% { box-shadow: 0 0 20px rgba(57, 255, 20, 0.8), 0 0 30px rgba(57, 255, 20, 0.4); }
}

:root[data-site-theme='alien-invasion'] button[class*='bg-primary'] {
  animation: alien-pulse-glow 2s ease-in-out infinite;
}

/* Glowing borders */
:root[data-site-theme='alien-invasion'] .border-primary {
  border-color: var(--site-primary);
  box-shadow: inset 0 0 10px rgba(57, 255, 20, 0.2);
}

/* Table row hover with glow */
:root[data-site-theme='alien-invasion'] tr:hover td {
  background-color: rgba(57, 255, 20, 0.05);
  box-shadow: inset 0 0 10px rgba(57, 255, 20, 0.1);
}

/* Badge styles - hexagonal shape */
:root[data-site-theme='alien-invasion'] .badge-alien {
  clip-path: polygon(25% 0%, 75% 0%, 100% 50%, 75% 100%, 25% 100%, 0% 50%);
  padding: 0.4rem 1rem;
  background: linear-gradient(135deg, var(--site-primary), var(--site-accent));
}

/* Starfield background effect - respects dark mode */
:root[data-site-theme='alien-invasion'] body,
:root[data-site-theme='alien-invasion'] main {
  background-image: 
    radial-gradient(2px 2px at 20% 30%, rgba(255, 255, 255, 0.8), transparent),
    radial-gradient(2px 2px at 60% 70%, rgba(255, 255, 255, 0.6), transparent),
    radial-gradient(1px 1px at 50% 50%, rgba(255, 255, 255, 0.4), transparent),
    radial-gradient(1px 1px at 80% 10%, rgba(255, 255, 255, 0.5), transparent),
    radial-gradient(2px 2px at 90% 60%, rgba(255, 255, 255, 0.7), transparent),
    radial-gradient(1px 1px at 33% 80%, rgba(255, 255, 255, 0.3), transparent);
  background-size: 200% 200%;
  background-color: var(--site-background);
  animation: alien-twinkle 20s ease-in-out infinite;
}

@keyframes alien-twinkle {
  0%, 100% { background-position: 0% 0%; }
  50% { background-position: 100% 100%; }
}

/* Sci-fi scan line effect (subtle) */
:root[data-site-theme='alien-invasion'] body::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, transparent, var(--site-primary), transparent);
  animation: alien-scan 4s linear infinite;
  opacity: 0.3;
  pointer-events: none;
  z-index: 9999;
}

@keyframes alien-scan {
  0% { top: 0%; }
  100% { top: 100%; }
}

/* Text glow for headings */
:root[data-site-theme='alien-invasion'] h1,
:root[data-site-theme='alien-invasion'] h2 {
  text-shadow: 0 0 10px rgba(57, 255, 20, 0.5);
}

/* Input focus glow */
:root[data-site-theme='alien-invasion'] input:focus,
:root[data-site-theme='alien-invasion'] textarea:focus {
  border-color: var(--site-primary);
  box-shadow: 0 0 15px rgba(57, 255, 20, 0.4);
}

/* Scrollbar styling */
:root[data-site-theme='alien-invasion'] ::-webkit-scrollbar {
  width: 10px;
  background: var(--site-surface);
}

:root[data-site-theme='alien-invasion'] ::-webkit-scrollbar-thumb {
  background: linear-gradient(180deg, var(--site-primary), var(--site-secondary));
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(57, 255, 20, 0.5);
}

:root[data-site-theme='alien-invasion'] ::-webkit-scrollbar-thumb:hover {
  box-shadow: 0 0 15px rgba(57, 255, 20, 0.8);
}
