customCSS:
/* Neon glow effects */
.challenge-card,
[data-theme='alien-invasion'] .rounded-lg.border {
  box-shadow: 0 0 10px rgba(57, 255, 20, 0.3);
  transition: all 0.3s ease;
}

.challenge-card:hover,
[data-theme='alien-invasion'] .rounded-lg.border:hover {
  box-shadow: 0 0 20px rgba(57, 255, 20, 0.5), 0 0 30px rgba(57, 255, 20, 0.3);
  transform: translateY(-2px);
}

/* Animated floating effect */
@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

[data-theme='alien-invasion'] .alien-float {
  animation: float 6s ease-in-out infinite;
}

/* Pulsing glow for primary buttons */
@keyframes pulse-glow {
  0%, 100% { box-shadow: 0 0 10px rgba(57, 255, 20, 0.5); }
  50% { box-shadow: 0 0 20px rgba(57, 255, 20, 0.8), 0 0 30px rgba(57, 255, 20, 0.4); }
}

[data-theme='alien-invasion'] button[class*='bg-primary'] {
  animation: pulse-glow 2s ease-in-out infinite;
}

/* Glowing borders */
[data-theme='alien-invasion'] .border-primary {
  border-color: var(--color-primary);
  box-shadow: inset 0 0 10px rgba(57, 255, 20, 0.2);
}

/* Table row hover with glow */
[data-theme='alien-invasion'] tr:hover td {
  background-color: rgba(57, 255, 20, 0.05);
  box-shadow: inset 0 0 10px rgba(57, 255, 20, 0.1);
}

/* Badge styles - hexagonal shape */
[data-theme='alien-invasion'] .badge-alien {
  clip-path: polygon(25% 0%, 75% 0%, 100% 50%, 75% 100%, 25% 100%, 0% 50%);
  padding: 0.4rem 1rem;
  background: linear-gradient(135deg, var(--color-primary), var(--color-accent));
}

/* Starfield background effect */
[data-theme='alien-invasion'] body,
[data-theme='alien-invasion'] main {
  background-image: 
    radial-gradient(2px 2px at 20% 30%, rgba(255, 255, 255, 0.8), transparent),
    radial-gradient(2px 2px at 60% 70%, rgba(255, 255, 255, 0.6), transparent),
    radial-gradient(1px 1px at 50% 50%, rgba(255, 255, 255, 0.4), transparent),
    radial-gradient(1px 1px at 80% 10%, rgba(255, 255, 255, 0.5), transparent),
    radial-gradient(2px 2px at 90% 60%, rgba(255, 255, 255, 0.7), transparent),
    radial-gradient(1px 1px at 33% 80%, rgba(255, 255, 255, 0.3), transparent);
  background-size: 200% 200%;
  background-color: var(--color-background);
  animation: twinkle 20s ease-in-out infinite;
}

@keyframes twinkle {
  0%, 100% { background-position: 0% 0%; }
  50% { background-position: 100% 100%; }
}

/* Sci-fi scan line effect (subtle) */
[data-theme='alien-invasion']::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, transparent, var(--color-primary), transparent);
  animation: scan 4s linear infinite;
  opacity: 0.3;
  pointer-events: none;
  z-index: 9999;
}

@keyframes scan {
  0% { top: 0%; }
  100% { top: 100%; }
}

/* Text glow for headings */
[data-theme='alien-invasion'] h1,
[data-theme='alien-invasion'] h2 {
  text-shadow: 0 0 10px rgba(57, 255, 20, 0.5);
}

/* Input focus glow */
[data-theme='alien-invasion'] input:focus,
[data-theme='alien-invasion'] textarea:focus {
  border-color: var(--color-primary);
  box-shadow: 0 0 15px rgba(57, 255, 20, 0.4);
}

/* Scrollbar styling */
[data-theme='alien-invasion'] ::-webkit-scrollbar {
  width: 10px;
  background: var(--color-surface);
}

[data-theme='alien-invasion'] ::-webkit-scrollbar-thumb {
  background: linear-gradient(180deg, var(--color-primary), var(--color-secondary));
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(57, 255, 20, 0.5);
}

[data-theme='alien-invasion'] ::-webkit-scrollbar-thumb:hover {
  box-shadow: 0 0 15px rgba(57, 255, 20, 0.8);
}
